/* Write your PL/SQL query statement below 
select to_char(sell_date ,'yyyy-mm-dd') sell_date, count(*),listagg(products,',') within group(order by products) from (select distinct sell_date, product from activities) group by sell_date;*/
select to_char(sell_date,'yyyy-mm-dd') sell_date  , count(*) num_sold , listagg(product,',') within group (order by sell_date) products from (select distinct sell_date, product from activities) group by sell_date;